import SwiftUI

//struture for wish list items to be viewed by WishlistView
struct WishlistItem: Identifiable {
    //variables for wish list items
    
    var id = UUID()
    var itemName: String
    var description: String
    var trendingPrice: Double
    var collectionType: String
    var imageURL: String
}

struct WishlistView: View {
    @State private var wishlistItems = [
        //examples of wish list items
        WishlistItem(itemName: "Batman Comic", description: "Batman Beyond Classic Comic Book", trendingPrice: 44.99, collectionType: "Comic", imageURL: "data:image/jpeg;base64,"),
        WishlistItem(itemName: "Invincible", description: "Graphic novel of super heroes", trendingPrice: 20.49, collectionType: "Comic", imageURL: "data:image/jpeg;base64,")
    ]
    
    //private variables for wish list items
    @State private var showAddItemSheet = false
    @State private var newItemName = ""
    @State private var newItemDescription = ""
    @State private var newCollectionType = ""
    @State private var newItemPrice = ""
    @State private var newItemImageURL = ""
    
    
    var body: some View {
        NavigationView {
            List {
                ForEach(wishlistItems) { item in
                    HStack {
                        AsyncImage(url: URL(string: item.imageURL)) { phase in
                            switch phase {
                            case .empty:
                                ProgressView()
                            case .success(let image):
                                image.resizable()
                                    .aspectRatio(contentMode: .fit)
                                    .frame(width: 100, height: 100)
                            case .failure:
                                Image(systemName: "photo") // Placeholder if the image fails to load
                                    .resizable()
                                    .aspectRatio(contentMode: .fit)
                                    .frame(width: 100, height: 100)
                            @unknown default:
                                EmptyView()
                            }
                        }
                        VStack(alignment: .leading) {
                            Text(item.itemName).font(.headline)
                            Text(item.description).font(.subheadline)
                            Text(item.collectionType).font(.subheadline)
                            Text(formatPrice(item.trendingPrice)).foregroundColor(.secondary)
                        }
                    }
                }
                .onDelete(perform: deleteItem)
            }
            .navigationBarTitle("Wishlist")
            .navigationBarItems(trailing:
                                    Button(action: {
                self.showAddItemSheet = true
            }) {
                Image(systemName: "plus")
            }
            )
            .sheet(isPresented: $showAddItemSheet) {
                VStack {
                    TextField("Item Name", text: self.$newItemName)
                    TextField("Description", text: self.$newItemDescription)
                    TextField("Collection Type", text: self.$newCollectionType)
                    TextField("Price", text: self.$newItemPrice)
                    TextField("Image URL", text: self.$newItemImageURL)
                    Button("Add") {
                        if let price = Double(self.newItemPrice) {
                            let newItem = WishlistItem(itemName: self.newItemName, description: self.newItemDescription, trendingPrice: price, collectionType: self.newCollectionType, imageURL: self.newItemImageURL)
                            self.wishlistItems.append(newItem)
                            self.showAddItemSheet = false
                        }
                    }
                }
                .padding()
            }
        }
    }
    
    func deleteItem(at offsets: IndexSet) {
        wishlistItems.remove(atOffsets: offsets)
    }
    
    func formatPrice(_ price: Double) -> String {
        let formatter = NumberFormatter()
        formatter.numberStyle = .currency
        formatter.maximumFractionDigits = 2
        return formatter.string(from: NSNumber(value: price)) ?? ""
    }
}

struct WishlistView_Previews: PreviewProvider {
    static var previews: some View {
        WishlistView()
    }
}
